fetch("https://raw.githubusercontent.com/Gvelet/QaHub-api/refs/heads/main/db/combined.json").then((t=>{if(!t.ok)throw new Error("Сеть ответила с ошибкой");return t.json()})).then((t=>{!function(t){const e=document.getElementById("testsWrapper");t.forEach((t=>{const s=Object.keys(t)[0],n=t[s],i=document.createElement("div");i.classList.add("tests__column"),i.innerHTML=`\n            <a class="tests-item" href="test.html?test=${s}">\n                <div class="tests__item-icon">\n                    <img src="./img/test-icon.png" alt="icon">\n                </div>\n                <div class="tests__item-content">\n                    <h3 class="tests__item-title">${n.title}</h3>\n                    <div class="tests__item-description">${n.briefDescription}</div>\n                </div>\n            </a>\n        `,e.appendChild(i)}))}(t)})).catch((t=>{console.error("Ошибка при загрузке JSON:",t)}));